    assert_eq!(b_biguint.is_infinity(), false);
    assert_eq!(b_biguint.is_undefined(), false);
    assert_eq!(b_biguint.is_divided_by_zero(), false);
    assert_eq!(b_biguint.is_left_carry(), false);
    assert_eq!(b_biguint.is_right_carry(), false);

