    pub fn panic_free_gcd(&self, other: &Self) -> Self
    {
        if self.is_zero() || other.is_zero()
        {
            let mut res = Self::zero();
            res.set_undefined();
            res
        }
        else
        {
            self.common_gcd(other)
        }
    }

