    pub fn panic_free_gcd_assign(&mut self, other: &Self)
    {
        calc_to_calc_assign!(self, Self::panic_free_gcd, other);
    }

    fn common_gcd(&self, other: &Self) -> Self
    {
        let mut x = self.clone();
        let mut y = Self::from_biguint(other);
        let mut t: Self;
        while !y.is_zero()
        {
            t = y;
            y = x.wrapping_rem(&t);
            x = t;
        }
        x
    }

